function on_submit() {
  var qatracker = SpreadsheetApp.openById('/*sheet id here*/').getSheets()[0];
  var trackcontents = qatracker.getRange(7, 7, qatracker.getLastRow(), 9).getValues();
  var empname = qatracker.getRange(4,3,3).getValue();
  var qaactivity = SpreadsheetApp.openById('/*sheet id here*/').getSheetByName(empname);
  Logger.log(qatracker.getLastRow())
  for (i=0; i<qatracker.getLastRow()-1;i++){
    qaactivity.appendRow(trackcontents[i])    
  }
qatracker.getRange(7, 7, qatracker.getLastRow(),6).clearContent()
qatracker.getRange(4, 3, 3).clearContent()
}
